Here's a simple example of how to load an image using SDL:

```c
#include <SDL.h>
#include <stdio.h>

SDL_Surface *load_image(const char *filename) {
    SDL_Surface *surface = NULL;
    SDL_Surface *optimized_surface = NULL;

    SDL_Surface *loaded_surface = SDL_LoadBMP(filename);
    if (loaded_surface == NULL) {
        printf("Unable to load image %s: %s\n", filename, SDL_GetError());
        return NULL;
    }

    // Convert the loaded surface to a format that OpenGL can use
    SDL_Surface *surface_format = SDL_AllocFormat(
        SDL_SWSURFACE,
        loaded_surface->w,
        loaded_surface->h,
        32,
        SDL_PIXELFORMAT_RGBA8888);

    if (surface_format == NULL) {
        SDL_Log("Unable to allocate surface for image %s", filename);
        SDL_FreeSurface(loaded_surface);
        return NULL;
    }

    // Convert the loaded surface to the desired format
    Uint32 rmask, gmask, bmask, amask;
    Uint32 r, g, b;

    SDL_LockSurface(loaded_surface);
    Uint32 *pixels = (Uint32 *)loaded_surface->pixels;
    SDL_LockSurface(surface_format);

    rmask = SDL_MapRGB(surface_format->format, 0xFF, 0x00, 0x00);
    gmask = SDL_MapRGB(surface_format->format, 0x00, 0xFF, 0x00);
    bmask = SDL_MapRGB(surface_format->format, 0x00, 0x00, 0xFF);
    amask = SDL_MapRGB(surface_format->format, 0x00, 0x00, 0x00);

    for (int i = 0; i < loaded_surface->h * loaded_surface->w; i++) {
        r = (loaded_surface->pixels[i] & 0xFF) << 16;
        g = (loaded_surface->pixels[i] & 0xFF00) >> 8;
        b = loaded_surface->pixels[i] & 0xFF0000;

        surface_format->pixels[i] = SDL_MapRGB(surface_format->format, r, g, b);
    }

    SDL_UnlockTextures();
    SDL_FreeSurface(loaded_surface);

    // Convert the surface to an OpenGL texture
    SDL_Surface *texture_surface = SDL_CreateRGBSurface(0, surface_format->w, surface_format->h, 32,
                                                       SDL_PIXELFORMAT_RGBA8888, NULL);
    SDL_BlitSurface(surface_format, NULL, texture_surface, NULL);
    SDL_FreeSurface(surface_format);

    // Create an OpenGL texture from the surface
    SDL_Surface *texture_surface_rgba = SDL_ConvertSurfaceFormat(texture_surface, SDL_PIXELFORMAT_ARGB8888);
    SDL_GL_CreateContextsRGBA(texture_surface_rgba->w, texture_surface_rgba->h, 24, 8, 8, 8, 0);
    SDL_GL_BindTexture(GL_TEXTURE_2D, SDL_GL_GetTexture(texture_surface_rgba->id));
    glTexImage2D(GL_TEXTURE_2D, 0, GL_RGBA, texture_surface_rgba->w, texture_surface_rgba->h, 0,
                 GL_RGBA, GL_UNSIGNED_BYTE, texture_surface_rgba->pixels);
    SDL_GL_DeleteTextures(1, &SDL_GL_GetTexture(texture_surface_rgba->id));
    SDL_FreeSurface(texture_surface_rgba);

    // Free the loaded image
    SDL_FreeSurface(loaded_surface);

    // Return the texture
    surface = SDL_ConvertSurfaceFormat(texture_surface, SDL_PIXELFORMAT_RGB565);
    SDL_FreeSurface(texture_surface);

    return surface;
}
```

