\usepackage{xcolor}
\usepackage{pgfsys-tikz}

\newcommand{\square}[1]{%
  \sbox0{%
    \pgfsys@beginscope
      \pgfsys@translate{#1}
      \pgfsys@setlength{fillcolor}{0.95\pgflinewidth}% Set the fill color
      \fill[#1] (0,0) rectangle (1,1);
    \pgfsys@endscope
  }
  \usebox{0}
}

\newcommand{\squareempty}[1]{%
  \sbox0{%
    \pgfsys@beginscope
      \pgfsys@translate{#1}
      \fill[white] (0,0) rectangle (1,1);
    \pgfsys@endscope
  }
  \usebox{0}
}

\newcommand{\drawsquare}[1]{%
  \ifnum #1=0
    \squareempty{#1}
  \else
    \square{#1}
  \fi
}

\newcommand{\drawrow}[1]{%
  \pgfmathsetmacro{\rowlength}{#1*1.732}% 1.732 is the square root of 2
  \pgfmathsetmacro{\rowoffset}{#1*0.866}% 0.866 is half the square root of 2
  \pgfmathsetmacro{\rowxshift}{-0.866}% Half a square width for alignment
  \pgfmathsetmacro{\rowyshift}{-\rowoffset}
  \pgfmathsetmacro{\rowx}{0}
  \foreach \i in {1,...,\numexpr#1\relax}{%
    \advance\rowx by \the\wd0pt%
    \pgfsys@beginscope
      \pgfsys@translate{\pgfpoint{\the\rowx+\rowxshift}{\the\rowy+\rowyshift}}
      \drawsquare{\the\numexpr\pdfuniformdeviate 100000}{}
      \advance\rowx by \the\wd0pt
    \pgfsys@endscope
  }
}

\newcommand{\drawboard}[1]{%
  \pgfmathsetmacro{\boardsize}{sqrt(length(#1)*2)}%
  \pgfmathsetmacro{\halfboardsize}{\boardsize/2}
  \pgfmathsetmacro{\boardxshift}{-\halfboardsize}
  \pgfmathsetmacro{\boardyshift}{-\halfboardsize}
  \pgfmathsetmacro{\boardx}{0}
  \pgfmathsetmacro{\boardy}{0}
  \foreach \row in {1,...,\numexpr length(#1)/5+1\relax}{%
