    \pgfsys@beginscope
      \pgfsys@translate{\pgfpoint{\the\boardx+\boardxshift}{\the\boardy+\boardyshift}}
      \drawrow{\the\numexpr\string#1:(\the\numexpr\numexpr\dimexpr\the\numexpr\number\numexpr\romannumeral\the\number\string :\relax):\relax}%
    \pgfsys@endscope
    \advance\boardx by \the\dimexpr\textwidth+\the\wd0pt%
